contracts/SecureGlobalWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title SecureGlobalWallet
 * @dev A secure global wallet that requires external admin approval for withdrawals
 * Only the owner (external admin wallet) can approve withdrawals
 * Deposits are handled automatically without requiring approval
 */
contract SecureGlobalWallet is Ownable, ReentrancyGuard, Pausable {
    IERC20 public usdtToken;

    // Withdrawal request structure
    struct WithdrawalRequest {
        address recipient;
        uint256 amount;
        bool approved;
        bool executed;
        uint256 timestamp;
        string reason;
    }

    // Mapping of withdrawal request ID to withdrawal request
    mapping(uint256 => WithdrawalRequest) public withdrawalRequests;
    uint256 public nextRequestId = 1;

    // Events
    event DepositReceived(address indexed from, uint256 amount, uint256 timestamp);
    event WithdrawalRequested(uint256 indexed requestId, address indexed recipient, uint256 amount, string reason);
    event WithdrawalApproved(uint256 indexed requestId, address indexed approver);
    event WithdrawalExecuted(uint256 indexed requestId, address indexed recipient, uint256 amount);
    event WithdrawalRejected(uint256 indexed requestId, address indexed rejector);
    event EmergencyWithdrawal(address indexed recipient, uint256 amount, address indexed executor);

    modifier onlyValidRequest(uint256 requestId) {
        require(requestId > 0 && requestId < nextRequestId, "Invalid request ID");
        require(!withdrawalRequests[requestId].executed, "Request already executed");
        _;
    }

    constructor(address _usdtToken) {
        require(_usdtToken != address(0), "Invalid USDT token address");
        usdtToken = IERC20(_usdtToken);
    }

    /**
     * @dev Receive deposits directly to the global wallet
     * Anyone can deposit, but only owner can approve withdrawals
     */
    function deposit(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(usdtToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        emit DepositReceived(msg.sender, amount, block.timestamp);
    }

    /**
     * @dev Request a withdrawal from the global wallet
     * Anyone can request, but only owner can approve
     */
    function requestWithdrawal(
        address recipient,
        uint256 amount,
        string memory reason
    ) external nonReentrant whenNotPaused returns (uint256 requestId) {
        require(recipient != address(0), "Invalid recipient address");
        require(amount > 0, "Amount must be greater than 0");
        require(amount <= getBalance(), "Insufficient balance");
        require(bytes(reason).length > 0, "Reason required");

        requestId = nextRequestId++;

        withdrawalRequests[requestId] = WithdrawalRequest({
            recipient: recipient,
            amount: amount,
            approved: false,
            executed: false,
            timestamp: block.timestamp,
            reason: reason
        });

        emit WithdrawalRequested(requestId, recipient, amount, reason);
        return requestId;
    }

    /**
     * @dev Approve a withdrawal request (only owner can call this)
     * This is the critical security function - only external admin can approve
     */
    function approveWithdrawal(uint256 requestId) external onlyOwner onlyValidRequest(requestId) {
        WithdrawalRequest storage request = withdrawalRequests[requestId];
        require(!request.approved, "Request already approved");
        require(request.amount <= getBalance(), "Insufficient balance");

        request.approved = true;
        emit WithdrawalApproved(requestId, msg.sender);
    }

    /**
     * @dev Execute an approved withdrawal
     * Can be called by anyone, but only works if owner has approved
     */
    function executeWithdrawal(uint256 requestId) external nonReentrant whenNotPaused onlyValidRequest(requestId) {
        WithdrawalRequest storage request = withdrawalRequests[requestId];
        require(request.approved, "Request not approved by owner");
        require(request.amount <= getBalance(), "Insufficient balance");

        request.executed = true;

        require(usdtToken.transfer(request.recipient, request.amount), "Transfer failed");

        emit WithdrawalExecuted(requestId, request.recipient, request.amount);
    }

    /**
     * @dev Reject a withdrawal request (only owner)
     */
    function rejectWithdrawal(uint256 requestId) external onlyOwner onlyValidRequest(requestId) {
        WithdrawalRequest storage request = withdrawalRequests[requestId];
        require(!request.approved, "Cannot reject approved request");

        request.executed = true; // Mark as executed to prevent further processing
        emit WithdrawalRejected(requestId, msg.sender);
    }

    /**
     * @dev Emergency withdrawal function (only owner)
     * For emergency situations where immediate withdrawal is needed
     */
    function emergencyWithdraw(address recipient, uint256 amount) external onlyOwner nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0 && amount <= getBalance(), "Invalid amount");

        require(usdtToken.transfer(recipient, amount), "Transfer failed");
        emit EmergencyWithdrawal(recipient, amount, msg.sender);
    }

    /**
     * @dev Get the current balance of the global wallet
     */
    function getBalance() public view returns (uint256) {
        return usdtToken.balanceOf(address(this));
    }

    /**
     * @dev Get withdrawal request details
     */
    function getWithdrawalRequest(uint256 requestId) external view returns (
        address recipient,
        uint256 amount,
        bool approved,
        bool executed,
        uint256 timestamp,
        string memory reason
    ) {
        require(requestId > 0 && requestId < nextRequestId, "Invalid request ID");
        WithdrawalRequest memory request = withdrawalRequests[requestId];
        return (
            request.recipient,
            request.amount,
            request.approved,
            request.executed,
            request.timestamp,
            request.reason
        );
    }

    /**
     * @dev Get all pending withdrawal requests (view function)
     */
    function getPendingRequests() external view returns (uint256[] memory) {
        uint256 count = 0;

        // First pass: count pending requests
        for (uint256 i = 1; i < nextRequestId; i++) {
            if (!withdrawalRequests[i].executed) {
                count++;
            }
        }

        // Second pass: collect pending request IDs
        uint256[] memory pendingIds = new uint256[](count);
        uint256 index = 0;

        for (uint256 i = 1; i < nextRequestId; i++) {
            if (!withdrawalRequests[i].executed) {
                pendingIds[index] = i;
                index++;
            }
        }

        return pendingIds;
    }

    /**
     * @dev Pause contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Transfer ownership to external admin wallet
     * This should be called after deployment to transfer control to your external wallet
     */
    function transferOwnershipToExternalAdmin(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid new owner address");
        require(newOwner != owner(), "Already the owner");
        _transferOwnership(newOwner);
    }
}

contracts/SecurePaymentProcessor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./SecureGlobalWallet.sol";

/**
 * @title SecurePaymentProcessor
 * @dev Enhanced payment processor that works with SecureGlobalWallet
 * Deposits go directly to the secure global wallet
 * Withdrawals require approval from the global wallet owner
 */
contract SecurePaymentProcessor is Ownable, ReentrancyGuard {
    IERC20 public usdtToken;
    SecureGlobalWallet public secureGlobalWallet;

    address public adminFeeWallet;

    uint256 public depositFeePercent = 2; // 2% fee on deposits
    uint256 public withdrawalFeePercent = 5; // 5% fee on withdrawals

    mapping(address => bool) public authorizedProcessors;
    mapping(string => bool) public processedTransactions;

    // Mapping to track user withdrawal requests
    mapping(address => uint256[]) public userWithdrawalRequests;

    event DepositProcessed(
        address indexed userWallet,
        string txHash,
        uint256 amount,
        uint256 adminFee,
        uint256 globalWalletAmount
    );

    event WithdrawalRequested(
        address indexed userWallet,
        uint256 requestId,
        uint256 amount,
        uint256 fee,
        uint256 netAmount
    );

    event WithdrawalCompleted(
        address indexed userWallet,
        uint256 requestId,
        uint256 netAmount
    );

    modifier onlyAuthorized() {
        require(authorizedProcessors[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    constructor(
        address _usdtToken,
        address _secureGlobalWallet,
        address _adminFeeWallet
    ) {
        require(_usdtToken != address(0), "Invalid USDT token address");
        require(_secureGlobalWallet != address(0), "Invalid global wallet address");
        require(_adminFeeWallet != address(0), "Invalid admin fee wallet address");

        usdtToken = IERC20(_usdtToken);
        secureGlobalWallet = SecureGlobalWallet(_secureGlobalWallet);
        adminFeeWallet = _adminFeeWallet;
    }

    /**
     * @dev Process deposit - sends funds to secure global wallet after taking admin fee
     */
    function processDeposit(
        address userWallet,
        string memory txHash,
        uint256 amount
    ) external onlyAuthorized nonReentrant {
        require(!processedTransactions[txHash], "Transaction already processed");
        require(amount > 0, "Amount must be greater than 0");

        // Mark transaction as processed
        processedTransactions[txHash] = true;

        // Calculate fees
        uint256 adminFee = (amount * depositFeePercent) / 100;
        uint256 globalWalletAmount = amount - adminFee;

        // Transfer admin fee to admin wallet
        require(usdtToken.transferFrom(userWallet, adminFeeWallet, adminFee), "Admin fee transfer failed");

        // Transfer remaining amount to secure global wallet
        require(usdtToken.transferFrom(userWallet, address(secureGlobalWallet), globalWalletAmount), "Global wallet transfer failed");

        emit DepositProcessed(userWallet, txHash, amount, adminFee, globalWalletAmount);
    }

    /**
     * @dev Request withdrawal - creates a withdrawal request in the secure global wallet
     */
    function requestWithdrawal(
        address userWallet,
        uint256 amount,
        string memory reason
    ) external onlyAuthorized nonReentrant returns (uint256 requestId) {
        require(amount > 0, "Amount must be greater than 0");

        // Calculate fees
        uint256 fee = (amount * withdrawalFeePercent) / 100;
        uint256 netAmount = amount - fee;

        // Create withdrawal request in secure global wallet
        // The request includes both the net amount to user and fee to admin
        string memory fullReason = string(abi.encodePacked(
            "Withdrawal for user: ", 
            addressToString(userWallet), 
            " | Reason: ", 
            reason
        ));

        requestId = secureGlobalWallet.requestWithdrawal(
            address(this), // This contract will receive the funds and distribute them
            amount, // Total amount including fee
            fullReason
        );

        // Track the request for the user
        userWithdrawalRequests[userWallet].push(requestId);

        emit WithdrawalRequested(userWallet, requestId, amount, fee, netAmount);
        return requestId;
    }

    /**
     * @dev Execute withdrawal after it's been approved by global wallet owner
     */
    function executeWithdrawal(
        uint256 requestId,
        address userWallet
    ) external onlyAuthorized nonReentrant {
        // Get withdrawal request details from global wallet
        (address recipient, uint256 amount, bool approved, bool executed, , ) = 
            secureGlobalWallet.getWithdrawalRequest(requestId);

        require(recipient == address(this), "Invalid request recipient");
        require(approved, "Request not approved by global wallet owner");
        require(!executed, "Request already executed");

        // Execute the withdrawal from global wallet to this contract
        secureGlobalWallet.executeWithdrawal(requestId);

        // Calculate distribution
        uint256 fee = (amount * withdrawalFeePercent) / 100;
        uint256 netAmount = amount - fee;

        // Distribute funds
        require(usdtToken.transfer(adminFeeWallet, fee), "Fee transfer failed");
        require(usdtToken.transfer(userWallet, netAmount), "User transfer failed");

        emit WithdrawalCompleted(userWallet, requestId, netAmount);
    }

    /**
     * @dev Get user's withdrawal requests
     */
    function getUserWithdrawalRequests(address user) external view returns (uint256[] memory) {
        return userWithdrawalRequests[user];
    }

    /**
     * @dev Get withdrawal request status
     */
    function getWithdrawalRequestStatus(uint256 requestId) external view returns (
        address recipient,
        uint256 amount,
        bool approved,
        bool executed,
        uint256 timestamp,
        string memory reason
    ) {
        return secureGlobalWallet.getWithdrawalRequest(requestId);
    }

    /**
     * @dev Add authorized processor
     */
    function addAuthorizedProcessor(address processor) external onlyOwner {
        authorizedProcessors[processor] = true;
    }

    /**
     * @dev Remove authorized processor
     */
    function removeAuthorizedProcessor(address processor) external onlyOwner {
        authorizedProcessors[processor] = false;
    }

    /**
     * @dev Update admin fee wallet
     */
    function updateAdminFeeWallet(address _adminFeeWallet) external onlyOwner {
        require(_adminFeeWallet != address(0), "Invalid admin fee wallet");
        adminFeeWallet = _adminFeeWallet;
    }

    /**
     * @dev Update fee percentages
     */
    function updateFees(uint256 _depositFee, uint256 _withdrawalFee) external onlyOwner {
        require(_depositFee <= 10 && _withdrawalFee <= 20, "Fees too high");
        depositFeePercent = _depositFee;
        withdrawalFeePercent = _withdrawalFee;
    }

    /**
     * @dev Get global wallet balance
     */
    function getGlobalWalletBalance() external view returns (uint256) {
        return secureGlobalWallet.getBalance();
    }

    /**
     * @dev Helper function to convert address to string
     */
    function addressToString(address _addr) internal pure returns (string memory) {
        bytes32 value = bytes32(uint256(uint160(_addr)));
        bytes memory alphabet = "0123456789abcdef";
        bytes memory str = new bytes(42);
        str[0] = '0';
        str[1] = 'x';
        for (uint256 i = 0; i < 20; i++) {
            str[2+i*2] = alphabet[uint8(value[i + 12] >> 4)];
            str[3+i*2] = alphabet[uint8(value[i + 12] & 0x0f)];
        }
        return string(str);
    }
}

